CREATE VIEW VW_C2_SENA_PROYECTO_DEPTOMONTOAPROBADO (NomCiudad, NomDepartamento, ValorRecomendado, Id_Proyecto, NomProyecto) AS SELECT T1.NomCiudad, T3.NomDepartamento, T4.ValorRecomendado, T5.Id_Proyecto, T5.NomProyecto FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, EvaluacionObservacion T4, Proyecto T5 WHERE T1.Id_Ciudad=T5.CodCiudad AND T2.CodProyecto=T5.Id_Proyecto AND T4.CodProyecto=T5.Id_Proyecto AND T3.Id_Departamento=T1.CodDepartamento AND T2.CodConvocatoria=2 AND T2.Viable=1