CREATE VIEW VW_C2_TAREAS (Contacto, Rol, CodConvocatoria, Descripcion, Fecha, Respuesta, FechaCierre) AS SELECT T1.Contacto, T1.Rol, T2.CodConvocatoria, T4.Descripcion, T5.Fecha, T5.Respuesta, T5.FechaCierre FROM ContactosRol T1, ConvocatoriaProyecto T2, TareaPrograma T3, TareaUsuario T4, TareaUsuarioRepeticion T5 WHERE T1.CodigoProyecto=T2.CodProyecto AND T1.id_Contacto=T4.CodContacto AND T4.Id_TareaUsuario=T5.CodTareaUsuario AND T3.Id_TareaPrograma=T4.CodTareaPrograma AND T2.CodConvocatoria=2