CREATE VIEW VW_C2_TOTALEMPLEOSGENERADOS (Generados, Joven, Desplazado, Madres, Minoria, Recluido, Desmovilizado, Discapacitado, Desvinculado) AS SELECT Sum(T1.Generado), Sum(T1.Joven), Sum(T1.Desplazado), Sum(T1.Madre), Sum(T1.Minoria), Sum(T1.Recluido), Sum(T1.Desmovilizado), Sum(T1.Discapacitado), Sum(T1.Desvinculado) FROM EmpleosGenerados T1, ConvocatoriaProyecto T2 WHERE T1.CodProyecto=T2.CodProyecto AND T2.CodConvocatoria=2