CREATE VIEW VW_C2_VALOR_SOLICITADO_EVALUACION (CodConvocatoria, Id_Proyecto, NomProyecto, CodEstado, Recursos) AS SELECT T1.CodConvocatoria, T2.Id_Proyecto, T2.NomProyecto, T2.CodEstado, T3.Recursos FROM ConvocatoriaProyecto T1, Proyecto T2, ProyectoFinanzasIngresos T3 WHERE T1.CodProyecto=T2.Id_Proyecto AND T3.CodProyecto=T2.Id_Proyecto AND T2.CodEstado=4 AND T1.CodConvocatoria=2