CREATE VIEW VW_C2_VAL_APROBADOS (CodConvocatoria, Viable, Nombre, Solicitado, Recomendado, Id_Proyecto, NomProyecto, nomTipoIndicador) AS SELECT T1.CodConvocatoria, T1.Viable, T2.Nombre, T2.Solicitado, T2.Recomendado, T3.Id_Proyecto, T3.NomProyecto, T4.nomTipoIndicador FROM ConvocatoriaProyecto T1, EvaluacionProyectoAporte T2, Proyecto T3, TipoIndicadorGestion T4 WHERE T1.CodProyecto=T3.Id_Proyecto AND T2.CodProyecto=T3.Id_Proyecto AND T2.CodTipoIndicador=T4.Id_TipoIndicador AND T1.Viable=1 AND T2.CodConvocatoria=2 AND T1.CodConvocatoria=2