CREATE VIEW VW_C2_VERIFICACION1 (CodProyecto, Viable, Id_Proyecto, NomProyecto, CodEstado, NomSector) AS SELECT T1.CodProyecto, T1.Viable, T2.Id_Proyecto, T2.NomProyecto, T2.CodEstado, T3.NomSector FROM ConvocatoriaProyecto T1, Proyecto T2, Sector T3, SubSector T4 WHERE T1.CodProyecto=T2.Id_Proyecto AND T2.CodSubSector=T4.Id_SubSector AND T3.Id_Sector=T4.CodSector AND T1.CodConvocatoria=2 AND T2.Id_Proyecto=6985