CREATE VIEW VW_C2_VIABLES_SECTOR_SUBSECTOR (NomCiudad, Viable, NomDepartamento, ValorRecomendado, Id_Proyecto, NomProyecto, Recursos, NomSector, NomSubSector) AS SELECT T1.NomCiudad, T2.Viable, T3.NomDepartamento, T4.ValorRecomendado, T5.Id_Proyecto, T5.NomProyecto, T6.Recursos, T7.NomSector, T8.NomSubSector FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, EvaluacionObservacion T4, Proyecto T5, ProyectoFinanzasIngresos T6, Sector T7, SubSector T8 WHERE T1.Id_Ciudad=T5.CodCiudad AND T1.CodDepartamento=T3.Id_Departamento AND T2.CodProyecto=T5.Id_Proyecto AND T4.CodProyecto=T5.Id_Proyecto AND T6.CodProyecto=T5.Id_Proyecto AND T8.Id_SubSector=T5.CodSubSector AND T8.CodSector=T7.Id_Sector AND T2.CodConvocatoria=2 AND T2.Viable=1 AND T4.CodConvocatoria=2