CREATE VIEW VW_C2_VIABLES_UNIDAD_EMPRENDIMIENTO (NomDepartamento, NomUnidad, Id_Proyecto, NomTipoInstitucion) AS SELECT T3.NomDepartamento, T4.NomUnidad, T5.Id_Proyecto, T6.NomTipoInstitucion FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Institucion T4, Proyecto T5, TipoInstitucion T6 WHERE T1.CodDepartamento=T3.Id_Departamento AND T1.Id_Ciudad=T5.CodCiudad AND T2.CodProyecto=T5.Id_Proyecto AND T4.Id_Institucion=T5.CodInstitucion AND T4.CodTipoInstitucion=T6.Id_TipoInstitucion AND T2.CodConvocatoria=2 AND T2.Viable=1