CREATE VIEW VW_C3_AVALADOS (NomConvocatoria, CodConvocatoria, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, Recursos, Fecha) AS SELECT T1.NomConvocatoria, T2.CodConvocatoria, T3.NomInstitucion, T3.NomUnidad, T4.Id_Proyecto, T4.NomProyecto, T5.Recursos, T6.Fecha FROM Convocatoria T1, ConvocatoriaProyecto T2, Institucion T3, Proyecto T4, ProyectoFinanzasIngresos T5, ProyectoFormalizacion T6 WHERE T1.Id_Convocatoria=T2.CodConvocatoria AND T2.CodProyecto=T4.Id_Proyecto AND T3.Id_Institucion=T4.CodInstitucion AND T5.CodProyecto=T4.Id_Proyecto AND T6.codProyecto=T4.Id_Proyecto AND T4.CodEstado>=3