CREATE VIEW VW_C3_CAQUETA (NomCiudad, NomConvocatoria, CodConvocatoria, NomDepartamento, Id_Proyecto, NomProyecto, Fecha) AS SELECT T1.NomCiudad, T2.NomConvocatoria, T3.CodConvocatoria, T4.NomDepartamento, T5.Id_Proyecto, T5.NomProyecto, T6.Fecha FROM Ciudad T1, Convocatoria T2, ConvocatoriaProyecto T3, departamento T4, Proyecto T5, ProyectoFormalizacion T6 WHERE T1.CodDepartamento=T4.Id_Departamento AND T2.Id_Convocatoria=T3.CodConvocatoria AND T3.CodProyecto=T5.Id_Proyecto AND T1.Id_Ciudad=T5.CodCiudad AND T5.Id_Proyecto=T6.codProyecto AND T4.Id_Departamento=18 AND T3.CodConvocatoria>=4 AND T5.CodEstado>=3