CREATE VIEW VW_C3_CONTACTOS_MPIO_DEPTO (NomCiudad, Nombres, Apellidos, Identificacion, Email, NomDepartamento, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, CodRol) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Email, T4.NomDepartamento, T5.NomInstitucion, T5.NomUnidad, T6.Id_Proyecto, T6.NomProyecto, T7.CodRol FROM Ciudad T1, Contacto T2, ConvocatoriaProyecto T3, departamento T4, Institucion T5, Proyecto T6, ProyectoContacto T7 WHERE T1.CodDepartamento=T4.Id_Departamento AND T1.Id_Ciudad=T6.CodCiudad AND T2.Id_Contacto=T7.CodContacto AND T3.CodProyecto=T6.Id_Proyecto AND T5.Id_Institucion=T6.CodInstitucion AND T6.Id_Proyecto=T7.CodProyecto AND T3.CodConvocatoria=4 AND T6.CodEstado=3