CREATE VIEW VW_C3_CONTACTO_PRUEBA_CONTRATACION (NomCiudad, Nombres, Apellidos, Identificacion, Email, Direccion, Telefono, NomDepartamento, ValorRecomendado, Id_Proyecto, NomProyecto) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Email, T2.Direccion, T2.Telefono, T4.NomDepartamento, T5.ValorRecomendado, T6.Id_Proyecto, T6.NomProyecto FROM Ciudad T1, Contacto T2, ConvocatoriaProyecto T3, departamento T4, EvaluacionObservacion T5, Proyecto T6, ProyectoContacto T7 WHERE T1.Id_Ciudad=T2.CodCiudad AND T3.CodProyecto=T6.Id_Proyecto AND T5.CodProyecto=T6.Id_Proyecto AND T4.Id_Departamento=T1.CodDepartamento AND T7.CodProyecto=T6.Id_Proyecto AND T2.Id_Contacto=T7.CodContacto AND T3.CodConvocatoria=4 AND T3.Viable=1 AND T5.CodConvocatoria=4 AND T7.CodRol=3 AND T7.Inactivo=0