CREATE VIEW VW_C3_DESEMPENO_EVALUADOR1 (Nombres, Apellidos, Identificacion, Puntajes, Id_Proyecto, NomProyecto, CodConvocatoria) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, Sum(T2.Puntaje), T4.Id_Proyecto, T4.NomProyecto, T5.CodConvocatoria FROM Contacto T1, EvaluacionEvaluador T2, Item T3, Proyecto T4, ProyectoContacto T5 WHERE T1.Id_Contacto=T5.CodContacto AND T2.CodProyecto=T4.Id_Proyecto AND T3.Id_Item=T2.CodItem AND T4.Id_Proyecto=T5.CodProyecto AND T5.CodRol=4 AND T5.Inactivo=0 GROUP BY T1.Nombres, T1.Apellidos, T1.Identificacion, T4.Id_Proyecto, T4.NomProyecto, T5.CodConvocatoria