CREATE VIEW VW_C3_EMPLEROSAGENERAR (Total, Generado, Joven, Desplazado, Madre, Minoria, Recluido, Desmovilizado, Discapacitado, Desvinculado, Id_Proyecto, NomProyecto) AS SELECT T1.Total, T1.Generado, T1.Joven, T1.Desplazado, T1.Madre, T1.Minoria, T1.Recluido, T1.Desmovilizado, T1.Discapacitado, T1.Desvinculado, T3.Id_Proyecto, T3.NomProyecto FROM EmpleosGenerados T1, ConvocatoriaProyecto T2, Proyecto T3 WHERE T1.CodProyecto=T2.CodProyecto AND T3.Id_Proyecto=T2.CodProyecto AND T2.CodConvocatoria=4 AND T2.Viable=1