CREATE VIEW VW_C3_EVALUACIONDESEMPENO3 (Nombres, Apellidos, CodItem, Puntaje, Id_Proyecto, NomProyecto) AS SELECT T1.Nombres, T1.Apellidos, T2.CodItem, T2.Puntaje, T3.Id_Proyecto, T3.NomProyecto FROM Contacto T1, EvaluacionEvaluador T2, Proyecto T3, ProyectoContacto T4 WHERE T1.Id_Contacto=T4.CodContacto AND T2.CodProyecto=T3.Id_Proyecto AND T4.CodProyecto=T3.Id_Proyecto AND T2.CodConvocatoria=4 AND T4.CodRol=4 AND T4.Inactivo=0