CREATE VIEW VW_C3_PAGOS_DETALLADOS (razonsocial, Id_Proyecto, Id_PagoActividad, Estado, CantidadDinero, FechaIngreso, FechaInterventor, FechaCoordinador, FechaRtaFA, ObservacionesFA, NumIdentificacion, Nombre, Apellido, RazonSocialBeneficiario) AS SELECT T1.razonsocial, T1.Id_Proyecto, T1.Id_PagoActividad, T1.Estado, T1.CantidadDinero, T1.FechaIngreso, T1.FechaInterventor, T1.FechaCoordinador, T1.FechaRtaFA, T1.ObservacionesFA, T1.NumIdentificacion, T1.Nombre, T1.Apellido, T1.RazonSocialBeneficiario FROM VISTA_PAGOS_DETALLADOS T1, ConvocatoriaProyecto T2 WHERE T1.Id_Proyecto=T2.CodProyecto AND T2.CodConvocatoria=4