CREATE VIEW VW_C3_PROYECCIONVENTAS (Precio, CodConvocatoria, Id_Proyecto, NomProyecto, CodEstado, Id_Producto, NomProducto, Sum_T5_Unidades) AS SELECT T1.Precio, T2.CodConvocatoria, T3.Id_Proyecto, T3.NomProyecto, T3.CodEstado, T4.Id_Producto, T4.NomProducto, Sum(T5.Unidades) FROM V_ProyectoProductoPrecio T1, ConvocatoriaProyecto T2, Proyecto T3, ProyectoProducto T4, ProyectoProductoUnidadesVentas T5 WHERE T1.CodProducto=T5.CodProducto AND T2.CodProyecto=T3.Id_Proyecto AND T4.CodProyecto=T3.Id_Proyecto AND T4.Id_Producto=T5.CodProducto AND T1.Periodo=1 AND T5.Ano=1 AND T3.CodEstado>=3 AND T3.CodEstado<=4 AND T2.CodConvocatoria=4 GROUP BY T1.Precio, T2.CodConvocatoria, T3.Id_Proyecto, T3.NomProyecto, T3.CodEstado, T4.Id_Producto, T4.NomProducto