CREATE VIEW VW_C3_SUMARIO (NomCiudad, NomDepartamento, Id_Proyecto, NomProyecto, Sumario) AS SELECT T1.NomCiudad, T3.NomDepartamento, T4.Id_Proyecto, T4.NomProyecto, T4.Sumario FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Proyecto T4 WHERE T1.Id_Ciudad=T4.CodCiudad AND T1.CodDepartamento=T3.Id_Departamento AND T2.CodProyecto=T4.Id_Proyecto AND T4.CodEstado>=3 AND T2.Viable=1