CREATE VIEW VW_C3_VIABLESSECTOR (NomCiudad, NomDepartamento, ValorRecomendado, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, NomSector) AS SELECT T1.NomCiudad, T3.NomDepartamento, T4.ValorRecomendado, T5.NomInstitucion, T5.NomUnidad, T6.Id_Proyecto, T6.NomProyecto, T8.NomSector FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, EvaluacionObservacion T4, Institucion T5, Proyecto T6, ProyectoFinanzasIngresos T7, Sector T8, SubSector T9 WHERE T1.Id_Ciudad=T6.CodCiudad AND T1.CodDepartamento=T3.Id_Departamento AND T2.CodProyecto=T6.Id_Proyecto AND T4.CodProyecto=T6.Id_Proyecto AND T5.Id_Institucion=T6.CodInstitucion AND T7.CodProyecto=T6.Id_Proyecto AND T9.Id_SubSector=T6.CodSubSector AND T8.Id_Sector=T9.CodSector AND T2.CodConvocatoria=4 AND T4.CodConvocatoria=4 AND T2.Viable=1 AND T6.Id_Proyecto=10842