CREATE VIEW VW_CERRADASINSTITUCION (NomConvocatoria, Fecha, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, Fechaaval) AS SELECT T1.NomConvocatoria, T2.Fecha, T3.NomInstitucion, T3.NomUnidad, T4.Id_Proyecto, T4.NomProyecto, T5.Fecha FROM Convocatoria T1, ConvocatoriaProyecto T2, Institucion T3, Proyecto T4, ProyectoFormalizacion T5 WHERE T1.Id_Convocatoria=T2.CodConvocatoria AND T2.CodProyecto=T4.Id_Proyecto AND T3.Id_Institucion=T4.CodInstitucion AND T5.codProyecto=T4.Id_Proyecto