CREATE VIEW VW_CERRADAS_CONTACTOS (NomCiudad, Nombres, Apellidos, Identificacion, Email, Direccion, Telefono, NomDepartamento, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, CodRol, Fecha, NomSector) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Email, T2.Direccion, T2.Telefono, T4.NomDepartamento, T5.NomInstitucion, T5.NomUnidad, T6.Id_Proyecto, T6.NomProyecto, T7.CodRol, T8.Fecha, T9.NomSector FROM Ciudad T1, Contacto T2, ConvocatoriaProyecto T3, departamento T4, Institucion T5, Proyecto T6, ProyectoContacto T7, ProyectoFormalizacion T8, Sector T9, SubSector T10 WHERE T1.Id_Ciudad=T6.CodCiudad AND T1.CodDepartamento=T4.Id_Departamento AND T2.Id_Contacto=T7.CodContacto AND T3.CodProyecto=T6.Id_Proyecto AND T5.Id_Institucion=T6.CodInstitucion AND T7.CodProyecto=T6.Id_Proyecto AND T8.codProyecto=T6.Id_Proyecto AND T10.Id_SubSector=T6.CodSubSector AND T9.Id_Sector=T10.CodSector AND T6.CodEstado>=3 AND T7.CodRol<=3 AND T7.Inactivo=0 AND T3.CodConvocatoria=5 AND T8.CodConvocatoria=5