CREATE VIEW VW_CONSULTADANI (Nombres, Apellidos, Identificacion, Email, Clave, Inactivo, NomConvocatoria, Id_Proyecto, NomProyecto, CodRol) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T1.Email, T1.Clave, T1.Inactivo, T2.NomConvocatoria, T4.Id_Proyecto, T4.NomProyecto, T5.CodRol FROM Contacto T1, Convocatoria T2, ConvocatoriaProyecto T3, Proyecto T4, ProyectoContacto T5 WHERE T1.Id_Contacto=T5.CodContacto AND T2.Id_Convocatoria=T3.CodConvocatoria AND T3.CodProyecto=T4.Id_Proyecto AND T5.CodProyecto=T4.Id_Proyecto