CREATE VIEW VW_CONSULTAPLNAESAVALADOS (NomCiudad, Nombres, Apellidos, Identificacion, Email, Direccion, Telefono, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, CodRol, Fecha, NomSector, NomSubSector) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Email, T2.Direccion, T2.Telefono, T4.NomInstitucion, T4.NomUnidad, T5.Id_Proyecto, T5.NomProyecto, T6.CodRol, T7.Fecha, T8.NomSector, T9.NomSubSector FROM Ciudad T1, Contacto T2, departamento T3, Institucion T4, Proyecto T5, ProyectoContacto T6, ProyectoFormalizacion T7, Sector T8, SubSector T9 WHERE T1.CodDepartamento=T3.Id_Departamento AND T1.Id_Ciudad=T5.CodCiudad AND T2.Id_Contacto=T6.CodContacto AND T4.Id_Institucion=T5.CodInstitucion AND T6.CodProyecto=T5.Id_Proyecto AND T7.codProyecto=T5.Id_Proyecto AND T9.Id_SubSector=T5.CodSubSector AND T9.CodSector=T8.Id_Sector AND T5.Id_Proyecto=11156