CREATE VIEW VW_CONSULTA_ASESORES (NomCiudad, Nombres, Apellidos, Viable, NomDepartamento, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, CodRol) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T3.Viable, T4.NomDepartamento, T5.NomInstitucion, T5.NomUnidad, T6.Id_Proyecto, T6.NomProyecto, T7.CodRol FROM Ciudad T1, Contacto T2, ConvocatoriaProyecto T3, departamento T4, Institucion T5, Proyecto T6, ProyectoContacto T7 WHERE T1.CodDepartamento=T4.Id_Departamento AND T2.CodInstitucion=T5.Id_Institucion AND T1.Id_Ciudad=T5.CodCiudad AND T3.CodProyecto=T6.Id_Proyecto AND T6.Id_Proyecto=T7.CodProyecto AND T2.Id_Contacto=T7.CodContacto AND T7.CodRol<3