CREATE VIEW VW_CONSULTA_VALOR_RECOMENDADO (NomConvocatoria, CodigoConvocatoria, CodConvocatoria, ValorRecomendado, Id_Proyecto, NomProyecto) AS SELECT T1.NomConvocatoria, T2.CodConvocatoria, T3.CodConvocatoria, T3.ValorRecomendado, T4.Id_Proyecto, T4.NomProyecto FROM Convocatoria T1, ConvocatoriaProyecto T2, EvaluacionObservacion T3, Proyecto T4 WHERE T1.Id_Convocatoria=T2.CodConvocatoria AND T2.CodProyecto=T4.Id_Proyecto AND T3.CodProyecto=T4.Id_Proyecto AND T2.Viable=1