CREATE VIEW VW_CONTACTOCONV4 (CodProyecto, FechaUltimoAval, NomCiudad, Nombres, Apellidos, Identificacion, Email, Direccion, Telefono, Inactivo, NomDepartamento, NomInstitucion, NomUnidad, NomProyecto, NomSector, NomSubSector) AS SELECT T1.CodProyecto, T1.FechaUltimoAval, T2.NomCiudad, T3.Nombres, T3.Apellidos, T3.Identificacion, T3.Email, T3.Direccion, T3.Telefono, T3.Inactivo, T4.NomDepartamento, T5.NomInstitucion, T5.NomUnidad, T6.NomProyecto, T7.NomSector, T8.NomSubSector FROM ContactoFechaFormalizacionProyecto T1, Ciudad T2, Contacto T3, departamento T4, Institucion T5, Proyecto T6, Sector T7, SubSector T8 WHERE T1.Id_Contacto=T3.Id_Contacto AND T1.CodProyecto=T6.Id_Proyecto AND T2.Id_Ciudad=T6.CodCiudad AND T2.CodDepartamento=T4.Id_Departamento AND T5.Id_Institucion=T6.CodInstitucion AND T8.Id_SubSector=T6.CodSubSector AND T8.CodSector=T7.Id_Sector AND T1.CodConvocatoria=32 AND T3.Inactivo=0