CREATE VIEW VW_CONTACTOPROYECTO (Id_Contacto, Nombres, Apellidos, Identificacion, Email, Clave, Inactivo, CodProyecto, CodRol, FechaInicio, FechaFin, Inactivo2) AS SELECT T1.Id_Contacto, T1.Nombres, T1.Apellidos, T1.Identificacion, T1.Email, T1.Clave, T1.Inactivo, T2.CodProyecto, T2.CodRol, T2.FechaInicio, T2.FechaFin, T2.Inactivo FROM Contacto T1, ProyectoContacto T2 WHERE T1.Id_Contacto=T2.CodContacto AND T2.CodProyecto=46482