CREATE VIEW VW_CONTACTOS (NomCiudad, Nombres, Apellidos, Inactivo, NomGrupo, NomInstitucion) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Inactivo, T3.NomGrupo, T5.NomInstitucion FROM Ciudad T1, Contacto T2, Grupo T3, GrupoContacto T4, Institucion T5 WHERE T2.CodInstitucion=T5.Id_Institucion AND T2.Id_Contacto=T4.CodContacto AND T3.Id_Grupo=T4.CodGrupo AND T1.Id_Ciudad=T5.CodCiudad