CREATE VIEW VW_CONTACTOS1 (NomInstitucion, NomUnidad, NomCiudad, Nombres, Apellidos, Identificacion, Cargo, Email, Clave, NomDepartamento, Id_Proyecto, NomProyecto) AS SELECT T1.NomInstitucion, T1.NomUnidad, T2.NomCiudad, T3.Nombres, T3.Apellidos, T3.Identificacion, T3.Cargo, T3.Email, T3.Clave, T4.NomDepartamento, T5.Id_Proyecto, T5.NomProyecto FROM Institucion T1, Ciudad T2, Contacto T3, departamento T4, Proyecto T5 WHERE T1.Id_Institucion=T3.CodInstitucion AND T2.CodDepartamento=T4.Id_Departamento AND T5.CodCiudad=T2.Id_Ciudad AND T5.CodContacto=T3.Id_Contacto