CREATE VIEW VW_CONTROL_EVALUACION_CONV4 (Nombres, Apellidos, CodConvocatoria, Viable, Id_Proyecto, NomProyecto, CodRol) AS SELECT T1.Nombres, T1.Apellidos, T2.CodConvocatoria, T2.Viable, T3.Id_Proyecto, T3.NomProyecto, T4.CodRol FROM Contacto T1, ConvocatoriaProyecto T2, Proyecto T3, ProyectoContacto T4 WHERE T1.Id_Contacto=T4.CodContacto AND T2.CodProyecto=T3.Id_Proyecto AND T4.CodProyecto=T3.Id_Proyecto AND T3.CodEstado=4 AND T4.CodRol=4 AND T2.CodConvocatoria=32 AND T4.Inactivo=0