CREATE VIEW VW_CONV57_AVALADOS (Nombres, Apellidos, Identificacion, Cargo, Email, Direccion, Telefono, Fax, NomConvocatoria, CodConvocatoria, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, Recursos, Fecha) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T1.Cargo, T1.Email, T1.Direccion, T1.Telefono, T1.Fax, T2.NomConvocatoria, T3.CodConvocatoria, T4.NomInstitucion, T4.NomUnidad, T5.Id_Proyecto, T5.NomProyecto, T6.Recursos, T7.Fecha FROM Contacto T1, Convocatoria T2, ConvocatoriaProyecto T3, Institucion T4, Proyecto T5, ProyectoFinanzasIngresos T6, ProyectoFormalizacion T7 WHERE T1.Id_Contacto=T2.CodContacto AND T1.Id_Contacto=T5.CodContacto AND T1.Id_Contacto=T7.codContacto AND T1.CodInstitucion=T4.Id_Institucion AND T1.CodInstitucion=T5.CodInstitucion AND T5.Id_Proyecto=T3.CodProyecto AND T5.Id_Proyecto=T6.CodProyecto AND T5.Id_Proyecto=T7.codProyecto AND T2.Id_Convocatoria=T3.CodConvocatoria AND T2.Id_Convocatoria=T7.CodConvocatoria AND T5.CodEstado=50