CREATE VIEW VW_CONV7RESULTADOSEVALUACION (Justificacion, Viable, ValorRecomendado, Id_Proyecto, NomProyecto, CodEstado, Recursos) AS SELECT T1.Justificacion, T1.Viable, T2.ValorRecomendado, T3.Id_Proyecto, T3.NomProyecto, T3.CodEstado, T4.Recursos FROM ConvocatoriaProyecto T1, EvaluacionObservacion T2, Proyecto T3, ProyectoFinanzasIngresos T4 WHERE T1.CodProyecto=T3.Id_Proyecto AND T2.CodProyecto=T3.Id_Proyecto AND T4.CodProyecto=T3.Id_Proyecto AND T2.CodConvocatoria=50 AND T1.CodConvocatoria=50