CREATE VIEW VW_CONVOCADOS (NomCiudad, CodConvocatoria, Fecha, Viable, NomDepartamento, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto) AS SELECT T1.NomCiudad, T2.CodConvocatoria, T2.Fecha, T2.Viable, T3.NomDepartamento, T4.NomInstitucion, T4.NomUnidad, T5.Id_Proyecto, T5.NomProyecto FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Institucion T4, Proyecto T5 WHERE T5.Id_Proyecto=T2.CodProyecto AND T5.CodCiudad=T1.Id_Ciudad AND T5.CodInstitucion=T4.Id_Institucion AND T1.CodDepartamento=T3.Id_Departamento