CREATE VIEW VW_CONVOCATORIANAL4 (CodProyecto, FechaUltimoAval, CodConvocatoria, Nombres, Apellidos, Email, NomProyecto, Nombre) AS SELECT T1.CodProyecto, T1.FechaUltimoAval, T1.CodConvocatoria, T2.Nombres, T2.Apellidos, T2.Email, T3.NomProyecto, T5.Nombre FROM ContactoFechaFormalizacionProyecto T1, Contacto T2, Proyecto T3, ProyectoContacto T4, Rol T5 WHERE T1.CodConvocatoria=32 AND T2.Id_Contacto=T4.CodContacto AND T4.CodRol=T5.Id_Rol AND T3.Id_Proyecto=T4.CodProyecto AND T1.CodProyecto=T3.Id_Proyecto