CREATE VIEW VW_CONVOCATORIAS_COFINANCIACION (NomCiudad, Id_Convocatoria, NomConvocatoria, Presupuesto, Cofinanciacion, NomDepartamento) AS SELECT T1.NomCiudad, T2.Id_Convocatoria, T2.NomConvocatoria, T2.Presupuesto, T3.Cofinanciacion, T4.NomDepartamento FROM Ciudad T1, Convocatoria T2, ConvocatoriaCofinanciacion T3, departamento T4 WHERE T1.Id_Ciudad=T3.CodCiudad AND T2.Id_Convocatoria=T3.CodConvocatoria AND T4.Id_Departamento=T1.CodDepartamento