CREATE VIEW VW_CONVOCATORIA_PROYECTOS (Id_Convocatoria, NomConvocatoria, FechaInicio, FechaFin, Fecha, Justificacion, Viable, Id_Proyecto, NomProyecto) AS SELECT T1.Id_Convocatoria, T1.NomConvocatoria, T1.FechaInicio, T1.FechaFin, T2.Fecha, T2.Justificacion, T2.Viable, T3.Id_Proyecto, T3.NomProyecto FROM Convocatoria T1, ConvocatoriaProyecto T2, Proyecto T3 WHERE T1.Id_Convocatoria=T2.CodConvocatoria AND T3.Id_Proyecto=T2.CodProyecto AND T3.Id_Proyecto=35926 AND T2.CodConvocatoria=80747