CREATE VIEW VW_CONVOCCERRADA (NomCiudad, NomDepartamento, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, Recursos, Fecha, NomSector) AS SELECT T1.NomCiudad, T3.NomDepartamento, T4.NomInstitucion, T4.NomUnidad, T5.Id_Proyecto, T5.NomProyecto, T6.Recursos, T7.Fecha, T8.NomSector FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Institucion T4, Proyecto T5, ProyectoFinanzasIngresos T6, ProyectoFormalizacion T7, Sector T8, SubSector T9 WHERE T1.Id_Ciudad=T5.CodCiudad AND T2.CodProyecto=T5.Id_Proyecto AND T3.Id_Departamento=T1.CodDepartamento AND T4.Id_Institucion=T5.CodInstitucion AND T6.CodProyecto=T5.Id_Proyecto AND T7.codProyecto=T5.Id_Proyecto AND T9.Id_SubSector=T5.CodSubSector AND T9.CodSector=T8.Id_Sector AND T2.CodConvocatoria=8 AND T7.CodConvocatoria=8