CREATE VIEW VW_CONV_AVALADOS (NomCiudad, CodDepartamento, Nombres, Apellidos, Identificacion, FechaNacimiento, Email, Telefono, InactivoUsuario, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, CodRol, Inactivo, Fecha, CodConvocatoria, NomSector, NomSubSector) AS SELECT T1.NomCiudad, T1.CodDepartamento, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.FechaNacimiento, T2.Email, T2.Telefono, T2.Inactivo, T3.NomInstitucion, T3.NomUnidad, T4.Id_Proyecto, T4.NomProyecto, T5.CodRol, T5.Inactivo, T6.Fecha, T6.CodConvocatoria, T7.NomSector, T8.NomSubSector FROM Ciudad T1, Contacto T2, Institucion T3, Proyecto T4, ProyectoContacto T5, ProyectoFormalizacion T6, Sector T7, SubSector T8 WHERE T1.Id_Ciudad=T4.CodCiudad AND T2.Id_Contacto=T5.CodContacto AND T3.Id_Institucion=T4.CodInstitucion AND T4.Id_Proyecto=T5.CodProyecto AND T4.Id_Proyecto=T6.codProyecto AND T4.CodSubSector=T8.Id_SubSector AND T7.Id_Sector=T8.CodSector AND T5.CodRol<=3 AND T6.CodConvocatoria=80