CREATE VIEW VW_CONV_RECOMENDADO_EJECUTADO (Id_Proyecto, NomProyecto, Id_Convocatoria, ValorRecomendado, AnnoConvocatoria, TotalRecomendado, DineroDesembolsado, NomConvocatoria) AS SELECT T1.Id_Proyecto, T1.NomProyecto, T1.Id_Convocatoria, T1.ValorRecomendado, T1.AnnoConvocatoria, T1.TotalRecomendado, T1.DineroDesembolsado, T2.NomConvocatoria FROM VISTA_RECURSOS_PROYECTOS_EJECUCION T1, Convocatoria T2 WHERE T1.Id_Convocatoria=T2.Id_Convocatoria