CREATE VIEW VW_COPIA_PRUEBA_AVALADOS_POR_SECTOR (NomCiudad, CodConvocatoria, NomDepartamento, Id_Proyecto, NomProyecto, Recursos, NomSector) AS SELECT T1.NomCiudad, T2.CodConvocatoria, T3.NomDepartamento, T4.Id_Proyecto, T4.NomProyecto, T5.Recursos, T6.NomSector FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Proyecto T4, ProyectoFinanzasIngresos T5, Sector T6, SubSector T7 WHERE T1.CodDepartamento=T3.Id_Departamento AND T1.Id_Ciudad=T4.CodCiudad AND T4.Id_Proyecto=T5.CodProyecto AND T2.CodProyecto=T4.Id_Proyecto AND T6.Id_Sector=T7.CodSector AND T4.CodSubSector=T7.Id_SubSector AND T2.CodConvocatoria=2 AND T4.CodEstado=3