CREATE VIEW VW_COSTOSVISITAINTERVENTORIACONINTERVENTOR (NomCiudad, razonsocial, codproyecto, Valor, NombreInforme, FechaSalida, FechaRegreso, CostoVisita, FechaInforme, NomMedioDeTransporte) AS SELECT T1.NomCiudad, T2.razonsocial, T2.codproyecto, T3.Valor, T4.NombreInforme, T4.FechaSalida, T4.FechaRegreso, T4.CostoVisita, T4.FechaInforme, T5.NomMedioDeTransporte FROM Ciudad T1, Empresa T2, InformeMedioTransporte T3, InformeVisitaInterventoria T4, MedioDeTransporte T5 WHERE T1.Id_Ciudad=T4.CodCiudadDestino AND T2.id_empresa=T4.CodEmpresa AND T3.CodInforme=T4.Id_Informe AND T3.CodMedioTransporte=T5.Id_MedioDeTransporte