CREATE VIEW VW_CRIONEGRO_EMPLEOS_GENERADOS (CodProyecto, Total, Generado, Joven, Desplazado, Madre, Minoria, Recluido, Desmovilizado, Discapacitado, Desvinculado, NomProyecto) AS SELECT T1.CodProyecto, T1.Total, T1.Generado, T1.Joven, T1.Desplazado, T1.Madre, T1.Minoria, T1.Recluido, T1.Desmovilizado, T1.Discapacitado, T1.Desvinculado, T3.NomProyecto FROM EmpleosGenerados T1, ConvocatoriaProyecto T2, Proyecto T3 WHERE T1.CodProyecto=T3.Id_Proyecto AND T2.CodProyecto=T3.Id_Proyecto AND T2.CodConvocatoria=11 AND T2.Viable=1