CREATE VIEW VW_C_CERRADAS (NomCiudad, NomConvocatoria, CodConvocatoria, NomDepartamento, Id_Proyecto, NomProyecto, Fecha, NomSector) AS SELECT T1.NomCiudad, T2.NomConvocatoria, T3.CodConvocatoria, T4.NomDepartamento, T5.Id_Proyecto, T5.NomProyecto, T6.Fecha, T7.NomSector FROM Ciudad T1, Convocatoria T2, ConvocatoriaProyecto T3, departamento T4, Proyecto T5, ProyectoFormalizacion T6, Sector T7, SubSector T8 WHERE T1.Id_Ciudad=T5.CodCiudad AND T1.CodDepartamento=T4.Id_Departamento AND T2.Id_Convocatoria=T3.CodConvocatoria AND T3.CodProyecto=T5.Id_Proyecto AND T6.codProyecto=T5.Id_Proyecto AND T8.Id_SubSector=T5.CodSubSector AND T8.CodSector=T7.Id_Sector AND T5.CodEstado>=3 AND T3.CodConvocatoria>=5 AND T6.CodConvocatoria>=5