CREATE VIEW VW_C_JEFES_ASESORES (NomCiudad, Nombres, Apellidos, NomDepartamento, NomInstitucion, NomUnidad, FechaFin, Id_Proyecto, NomProyecto) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T3.NomDepartamento, T4.NomInstitucion, T4.NomUnidad, T5.FechaFin, T6.Id_Proyecto, T6.NomProyecto FROM Ciudad T1, Contacto T2, departamento T3, Institucion T4, InstitucionContacto T5, Proyecto T6, ProyectoContacto T7 WHERE T1.CodDepartamento=T3.Id_Departamento AND T7.CodRol<3 AND T1.Id_Ciudad<T4.CodCiudad AND T6.CodInstitucion<T4.Id_Institucion AND T4.Id_Institucion<T5.CodInstitucion AND T5.CodContacto<T2.Id_Contacto AND T6.CodInstitucion<T4.Id_Institucion AND T7.CodProyecto<T6.Id_Proyecto AND T7.Inactivo=0 AND T5.FechaFin=NULL