CREATE VIEW VW_C_PAGOS (razonsocial, ValorRecomendado, N_Desembolsos, Valor_Desembolsado, Id_Proyecto, NomProyecto) AS SELECT T3.razonsocial, T4.ValorRecomendado, Count(T5.Id_PagoActividad), Sum(T5.CantidadDinero), T6.Id_Proyecto, T6.NomProyecto FROM Convocatoria T1, ConvocatoriaProyecto T2, Empresa T3, EvaluacionObservacion T4, PagoActividad T5, Proyecto T6 WHERE T1.Id_Convocatoria=T2.CodConvocatoria AND T2.CodProyecto=T6.Id_Proyecto AND T3.codproyecto=T6.Id_Proyecto AND T4.CodProyecto=T6.Id_Proyecto AND T1.encargofiduciario=103806 AND T2.CodConvocatoria=1 AND T4.CodConvocatoria=1 AND T3.codproyecto=T5.CodProyecto AND T5.Estado=4 GROUP BY T3.razonsocial, T4.ValorRecomendado, T6.Id_Proyecto, T6.NomProyecto