CREATE VIEW VW_DATOS_EMPRENDEDORE (NomCiudad, Departamento, Unidad, Id_Proyecto, Proyecto, Valor_Solicitado, FechaAval, Aval, CodConvocatoria, NomSector) AS SELECT T1.NomCiudad, T2.NomDepartamento, T3.NomUnidad, T4.Id_Proyecto, T4.NomProyecto, T5.Recursos, T6.Fecha, T6.Aval, T6.CodConvocatoria, T7.NomSector FROM Ciudad T1, departamento T2, Institucion T3, Proyecto T4, ProyectoFinanzasIngresos T5, ProyectoFormalizacion T6, Sector T7, SubSector T8 WHERE T1.CodDepartamento=T2.Id_Departamento AND T1.Id_Ciudad=T4.CodCiudad AND T4.CodInstitucion=T3.Id_Institucion AND T4.Id_Proyecto=T5.CodProyecto AND T4.Id_Proyecto=T6.codProyecto AND T4.CodSubSector=T8.Id_SubSector AND T7.Id_Sector=T8.CodSector AND T6.CodConvocatoria=2 AND T4.CodEstado=3