CREATE VIEW VW_DATOS_EMPRESAS (NomCiudad, Nombres, Apellidos, Email, Telefono, NomDepartamento, razonsocial, ObjetoSocial, DomicilioEmpresa, Tel1, Mail1, Nit, Sumario, NomSector, NomSubSector) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Email, T2.Telefono, T3.NomDepartamento, T4.razonsocial, T4.ObjetoSocial, T4.DomicilioEmpresa, T4.Telefono, T4.Email, T4.Nit, T6.Sumario, T7.NomSector, T8.NomSubSector FROM Ciudad T1, Contacto T2, departamento T3, Empresa T4, EmpresaContacto T5, Proyecto T6, Sector T7, SubSector T8 WHERE T1.CodDepartamento=T3.Id_Departamento AND T1.Id_Ciudad=T4.CodCiudad AND T2.Id_Contacto=T5.codcontacto AND T4.codproyecto=T6.Id_Proyecto AND T4.id_empresa=T5.codempresa AND T6.CodSubSector=T8.Id_SubSector AND T7.Id_Sector=T8.CodSector