CREATE VIEW VW_DIRECTORIO_EMPRENDEDORES (Nombres, Apellidos, Identificacion, Email, Id_Proyecto, CodContacto, CodRol, CodConvocatoria) AS SELECT T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Email, T5.Id_Proyecto, T6.CodContacto, T6.CodRol, T6.CodConvocatoria FROM Ciudad T1, Contacto T2, departamento T3, Empresa T4, Proyecto T5, ProyectoContacto T6 WHERE T1.CodDepartamento=T3.Id_Departamento AND T2.Id_Contacto=T6.CodContacto AND T1.Id_Ciudad=T5.CodCiudad AND T4.codproyecto=T5.Id_Proyecto AND T5.Id_Proyecto=T6.CodProyecto AND T6.CodRol=3 AND T5.CodEstado=7