CREATE VIEW VW_DIRECTORIO_EMPRESAS (NomCiudad, Nombres, Apellidos, NomDepartamento, razonsocial, ObjetoSocial, DomicilioEmpresa, Telefono, Email, RepresentanteLegal, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, NomSector) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T3.NomDepartamento, T4.razonsocial, T4.ObjetoSocial, T4.DomicilioEmpresa, T4.Telefono, T4.Email, T5.RepresentanteLegal, T6.NomInstitucion, T6.NomUnidad, T7.Id_Proyecto, T7.NomProyecto, T8.NomSector FROM Ciudad T1, Contacto T2, departamento T3, Empresa T4, EmpresaContacto T5, Institucion T6, Proyecto T7, Sector T8, SubSector T9 WHERE T1.Id_Ciudad=T7.CodCiudad AND T2.Id_Contacto=T5.codcontacto AND T3.Id_Departamento=T1.CodDepartamento AND T4.codproyecto=T7.Id_Proyecto AND T4.id_empresa=T5.codempresa AND T6.Id_Institucion=T7.CodInstitucion AND T9.Id_SubSector=T7.CodSubSector AND T8.Id_Sector=T9.CodSector