CREATE VIEW VW_EDAD1 (Id_Contacto, Nombres, Apellidos, Identificacion, FechaNacimiento, Email, Viable, Id_Grupo, NomGrupo, Id_Proyecto, NomProyecto) AS SELECT T1.Id_Contacto, T1.Nombres, T1.Apellidos, T1.Identificacion, T1.FechaNacimiento, T1.Email, T2.Viable, T3.Id_Grupo, T3.NomGrupo, T5.Id_Proyecto, T5.NomProyecto FROM Contacto T1, ConvocatoriaProyecto T2, Grupo T3, GrupoContacto T4, Proyecto T5 WHERE T1.Id_Contacto=T5.CodContacto AND T2.CodProyecto=T5.Id_Proyecto AND T4.CodContacto=T1.Id_Contacto AND T3.Id_Grupo=T4.CodGrupo